import React, { useState, useEffect } from "react";
import {getBraintreeClientToken} from "./apiCore"
import { isAuthenticated } from "../Auth";
import { Link } from "react-router-dom";

const Checkout = ({ products }) => {
    const [data, setData] = useState({
        success: false,
        clientToken: null,
        error: '',
        instance: {},
        address: ''
    })

    // Make a request to the backend
    const userId = isAuthenticated() && isAuthenticated().user._id
    const token = isAuthenticated() && isAuthenticated().token

    const getTotal = () => {
        return products.reduce((currentValue, nextValue) => {
            return currentValue + nextValue.count * nextValue.price;
        }, 0);
    };

    const showCheckout = () => {
        return isAuthenticated() ? (
            <button style={{backgroundColor: '#4B3655', color: 'white'}} className="btn">
                <i class="fa fa-shopping-bag" style={{fontSize: '20px', color:'white', border: "1px solid #4B3655 "}}></i> Checkout
            </button>
        ) : (
            <Link to="/signin">
                <button className="btn">Sign in to checkout</button>
            </Link>
        );
    };

    return (
        <div>
            <h2>Total: <span style={{color: "green"}}> ${getTotal()} </span></h2>

            {showCheckout()}
        </div>
    );
};

export default Checkout;
